import sys,math
class Eratos:
	def __init__(A,N):A.N=N;A._primes={A:True for A in range(2,N+1)}
	def filter(A):
		C=A.N
		if A.N>5:C=math.ceil(A.N**.5)+1
		for B in range(2,C):
			if not A._primes[B]:continue
			for D in range(B*2,A.N+1,B):
				if D in A._primes:A._primes[D]=False
	def primes(A,x):return[A for(A,B)in A._primes.items()if B==True and A<=x]
def run():
	A=Eratos(999999);A.filter()
	for B in sys.stdin:C=int(B);print(len(A.primes(C)))
if __name__=='__main__':run()

