from heapq import heappop,heappush
def dijkstra(links,n,m,freezables,s,t):
	C=[(0,0,s,m)];H=[0]*n
	while C:
		D,F,E,A=heappop(C)
		if E==t:
			if D<=m:return D
			return F-A
		if H[E]>=A:continue
		H[E]=A
		for(B,G)in links[E]:
			if A<B:continue
			if G in freezables:heappush(C,(D+B,F+B*2+m-A,G,m))
			else:heappush(C,(D+B,F+B,G,A-B))
	return'Help!'
