import math,sys
sys.setrecursionlimit(10**6)
class SegmentTree:
	def __init__(A,size,func=lambda x,y:x+y,default=0):'\n        size : 配列のサイズ\n        func : queryの内容(e.g. 区間和ならf(x, y)=x+y)\n        default : 単位元\n        ';B=default;A.size=2**(size-1).bit_length();A.default=B;A.dat=[B for A in range(A.size*2-1)];A.f=func
	def update(A,i,x):
		'\n        i番目の値をxに変更する。0-indexedなので注意\n        ';i=i+A.size-1;A.dat[i]=x
		while i>0:i=i-1>>1;A.dat[i]=A.f(A.dat[i*2+1],A.dat[i*2+2])
	def query(A,a,b,k=0,L=0,R=None):
		'\n        区間[a, b]に関するクエリを処理します。\n        例えばi=1,2,3に関して処理したいなら外からquery(1, 3+1)と呼ぶ必要があります。\n        '
		if R is None:R=A.size
		if R<=a or b<=L:return A.default
		if a<=L and R<=b:return A.dat[k]
		else:B=A.query(a,b,2*k+1,L,L+R>>1);C=A.query(a,b,2*k+2,L+R>>1,R);return A.f(B,C)
