	A=0
	while n>0:A+=n%b;n//=b
	return A
def main():
	D=int(input());E=int(input());F=-1
	if D<E:F=-1
	elif D==E:F=D+1
	else:
		H=[];A,G=D,1
		for B in range(1,D):
